import { Button,  Checkbox,  FormControlLabel,  FormLabel,  TextField } from '@mui/material';
import { Box } from '@mui/system';
import React, { useEffect, useState} from 'react'
import { useParams } from 'react-router';
import axios from 'axios';

const BookDetail = () => {
    const [inputs, setInputs] = useState({});
    const id= useParams().id;
    const [checked, setChecked] = useState(false);
    useEffect(() => {  
        const fetchHandler = async() =>{
        await axios.get(`http://localhost:500/${id}`)
        .then((res)=> res.data)
        .then((data) => setInputs(data.book));
        };
        fetchHandler()
     }, [id]);

     const sendRequest = async() =>{
      await axios.put(`http://localhost:500/${id}`,{
         author: String(inputs.author),
    description :String(inputs.description),
    price :Number(inputs.price),
    image :String(inputs.image),
    available :Boolean(checked)
      }
      

      )

     }

     const handleSubmit=(e)=> {
        e.preventDefault();
     }
     const handleChange=(e)=>{
        setInputs((prevState) => ({
            ...prevState,
            [e.target.name]:e.target.value,
          }));        
     };
  
  return(
  <div> 
  {inputs && (
  <form onSubmit={handleSubmit}> 
  <Box
  display="flex"
  flexDirection="column"
  justifyContent={"center"}
  maxWidth={700}
  alignContent={"center"}
  alignSelf={"center"}
  marginLeft={"auto"}
  marginRight={"auto"} 
  marginTop={10}
  > 
  <FormLabel> Name </FormLabel>
  <TextField value={inputs.name}
  onChange={handleChange}
  margin='normal' fullWidth variant='outlined' name='name'/> 
  <FormLabel> Author </FormLabel>
  <TextField 
   value={inputs.author}
  onChange={handleChange}
  margin='normal' fullWidth variant='outlined' name='author'/> 
  <FormLabel> Description </FormLabel>
  <TextField 
   value={inputs.description}
  onChange={handleChange}
  margin='normal'
  fullWidth variant='outlined' 
   name='description'/> 
  
  <FormLabel> Price </FormLabel>
  <TextField 
   value={inputs.price}
  onChange={handleChange}
  type="number" margin='normal' fullWidth variant='outlined' name='price'/> 
  <FormLabel> Image </FormLabel>
  <TextField 
   value={inputs.image}
  onChange={handleChange}
  margin='normal' fullWidth variant='outlined' name='image'/> 
  <FormControlLabel 
  control={<Checkbox checked={checked} onChange={()=> setChecked(!checked)} />} 
  label="Available" />
  <Button variant='contained' type="submit"> Update Book </Button>  
  </Box>
  </form>
  )};
   </div>
  );
};

export default BookDetail;
