import { Button, FormLabel, TextField } from '@mui/material'
import { Box, Checkbox, FormControlLabel } from '@mui/system';
import { useState } from 'react';
import React from 'react';

const AddBook = () => {
  const [inputs, setInputs] = useState({
name:'',
description:'',
price:'',
author:'',
available:false,
image:'',
});
  return <form > 
  <Box
  display="flex"
  flexDirection="column"
  justifyContent={"center"}
  maxWidth={700}
  alignContent={"center"}
  alignSelf={"center"}
  marginLeft={"auto"}
  marginRight={"auto"} 
  marginTop={10}
   > 
  <FormLabel> Name </FormLabel>
  <TextField 
  value={inputs.name}
  onChange={handleChange}
  margin='normal' fullWidth variant='outlined' name='name'/> 
  <FormLabel> Author </FormLabel>
  <TextField 
   value={inputs.name}
  onChange={handleChange}
  margin='normal' fullWidth variant='outlined' name='author'/> 
  <FormLabel> Description </FormLabel>
  <TextField 
  margin='normal'
   fullWidth variant='outlined' 
   name='description'/> 
  
  <FormLabel> Price </FormLabel>
  <TextField type="number" margin='normal' fullWidth variant='outlined' name='price'/> 
  <FormLabel> Image </FormLabel>
  <TextField type="" margin='normal' fullWidth variant='outlined' name='image'/> 
  <FormControlLabel control={<Checkbox defaultChecked />} label="Available" />
  <Button variant='contained' type="submit"> Add Book </Button>  
  </Box>
  </form>
}

export default AddBook
