        const fetchHandler = async() =>{
        await axios.get(`http://localhost:500/${id}`)
        .then((res)=> res.data)
        .then((data) => setInputs(data.book));
        };
        fetchHandler()
     }, [id]);

     const sendRequest = async() =>{
      await axios.put(`http://localhost:500/${id}`,{
      name:String(inputs.name),   
